        public static string GenerateParagraphs(int number)
        {
            if (number <= 0)
            {
                return string.Empty;
            }

            string lorem = mainParagraph;
            int n = number - 1;

            for (int i = 0; i < n; i++)
            {
                int ind = i % additionalParagraphs.Length;
                lorem += "\n\n" + additionalParagraphs[ind].Trim();
            }

            lorem = lorem.Trim();

            return lorem;
        }

        public static string GenerateWords(int number)
        {
            if (number <= 0)
            {
                return string.Empty;
            }

            string[] main = mainParagraph.Split(" ");
            string lorem = main[0];
            int n = Mathf.Min(number, main.Length);

            for (int i = 1; i < n; i++)
            {
                lorem += " " + main[i].Trim();
            }

            int alreadyGenerated = n;
            int virtualInd = 0;

            while (alreadyGenerated < number)
            {
                int ind = virtualInd % additionalParagraphs.Length;

                string[] additional = additionalParagraphs[ind].Split(" ");
                n = Mathf.Min(number - alreadyGenerated, additional.Length);

                for (int i = 0; i < n; i++)
                {
                    lorem += " " + additional[i].Trim();
                }

                alreadyGenerated += n;
                virtualInd++;
            }

            lorem = lorem.Trim();

            char lastChar = lorem[lorem.Length - 1];
            char[] validEndChars = { '.', '!', '?'};

            if (!validEndChars.Contains(lastChar))
            {
                lorem += '.';
            }

            return lorem;
        }
